document.addEventListener("DOMContentLoaded", () => {
    document.querySelector(".header__nav").insertAdjacentHTML("afterend", '\n        <div class="header__control row">\n            <label for="toggle" class="theme-toggler settings-option" id="theme-toggler">\n                <span class="theme-toggler__label">Тема</span>\n                <input type="checkbox" id="toggle" class="theme-toggler__checkbox" checked="">\n                <span aria-hidden="true" class="theme-toggler__switch"></span>\n            </label>\n        </div>\n    ');
    const e = document.querySelector('.theme-toggler input[type="checkbox"]'),
        t = document.documentElement,
        n = localStorage.getItem("theme") || "light";
    "light" == n ? e.removeAttribute("checked") : e.checked = "true", t.setAttribute("data-theme", n), e.addEventListener("change", e => {
        e.target.checked ? (t.setAttribute("data-theme", "dark"), localStorage.setItem("theme", "dark")) : (t.setAttribute("data-theme", "light"), localStorage.setItem("theme", "light"))
    }, !1);
    if (document.URL === 'https://onemoment.cc/contest') {
        const mainWrap = document.querySelector('.main-wrap');
        mainWrap.classList.add('main-wrap--active');
    }
    if (document.URL === 'https://onemoment.cc/rules') {
        document.querySelectorAll('.case .text h5').forEach(item => {
            item.addEventListener('click', (event) => {
                event.target.classList.toggle('activeAccodionTrigger');
                event.target.nextElementSibling.classList.toggle('activeAccordion');
            });
        })
    }
});
